import java.io.FileNotFoundException;import java.io.FileOutputStream;import java.io.PrintWriter;import java.text.DecimalFormat;public class M_log {    public static PrintWriter printWriter;    static {        try {            printWriter = new PrintWriter(new FileOutputStream("M.log"));        } catch (FileNotFoundException e) {            e.printStackTrace();        }    }    public static void log(Object... param) {        StringBuilder sb = new StringBuilder();        if (param != null && param.length > 0) {            for (int i = 0; i < param.length; i++) {                Float aFloat = (Float)param[i];                DecimalFormat fnum = new DecimalFormat("0.00");                String dd=fnum.format(aFloat);                sb.append( String.format("%15s",dd)+ "\t\t ");            }        } else {            sb.append("null");        }        printWriter.println(sb.toString());        printWriter.flush();    }}