# -*- coding:utf-8 -*-#!/usr/bin/python3'''用于检测下载的文件参数'''import datetimeimport osfrom netCDF4 import Datasetdef getday(y,m,d,n):    the_date = datetime.datetime(y,m,d)    result_date = the_date + datetime.timedelta(days=n)    d = result_date.strftime('%Y-%m-%d')    return dpath = os.listdir(os.getcwd())for p in path:    if  p=='check.py':        continue    n=Dataset(p)    hours=(n.variables['time'][0])    print(p+"-->",getday(1900,1,1,hours/24),n.variables['latitude'][0],n.variables['longitude'][0],n.variables['latitude'][-1],n.variables['longitude'][-1])os.system("pause");